css:
@font-face {
  font-family: "Press Start 2P";
  src: url("atom://activate-power-mode/fonts/PressStart2P-Regular.ttf") format("truetype");
}
atom-text-editor::shadow .power-mode-canvas,
.power-mode-canvas {
  position: absolute;
  z-index: 10;
  top: 0;
  left: 0;
  pointer-events: none;
}
atom-text-editor::shadow .streak-container,
.streak-container {
  font-family: "Press Start 2P", sans-serif;
  position: absolute;
  z-index: 10;
  right: 20px;
  top: 5px;
  text-align: right;
}
atom-text-editor::shadow .streak-container .title,
.streak-container .title {
  color: white;
  text-shadow: 2px 2px 2px #4EFFA1;
  font-size: 16px;
}
atom-text-editor::shadow .streak-container .bar,
.streak-container .bar {
  position: relative;
  opacity: 0.5;
  margin-top: 10px;
  height: 8px;
  background: #4EFFA1;
  width: 100%;
  transform: scaleX(0);
}
atom-text-editor::shadow .streak-container .counter,
.streak-container .counter {
  margin-top: 30px;
  font-size: 80px;
  color: #4EFFA1;
  line-height: 1;
}
atom-text-editor::shadow .streak-container .counter.bump,
.streak-container .counter.bump {
  animation: grow 0.25s both;
}
atom-text-editor::shadow .streak-container .exclamations,
.streak-container .exclamations {
  position: absolute;
  bottom: -20px;
  right: 0;
  display: block;
  color: #4EFFA1;
  opacity: 0.75;
  font-size: 20px;
  text-align: right;
}
atom-text-editor::shadow .streak-container .exclamations .exclamation,
.streak-container .exclamations .exclamation {
  right: 0;
  top: 0;
  display: block;
  position: absolute;
  min-width: 200px;
  animation: exclamation 1.5s ease-out both;
}
atom-text-editor::shadow .streak-container.reached .title,
.streak-container.reached .title {
  text-shadow: 2px 2px 2px #00DDFF;
}
atom-text-editor::shadow .streak-container.reached .counter,
.streak-container.reached .counter,
atom-text-editor::shadow .streak-container.reached .exclamations,
.streak-container.reached .exclamations {
  color: #00DDFF;
}
atom-text-editor::shadow .streak-container.reached .bar,
.streak-container.reached .bar {
  background: #00DDFF;
}
