atom-text-editor::shadow .gutter .line-number.split-diff-added,
atom-text-editor .line.split-diff-added,
atom-text-editor::shadow .line.split-diff-added {
  background-color: rgba(67, 208, 138, 0.4);
}
atom-text-editor .gutter .line-number.split-diff-removed,
atom-text-editor::shadow .gutter .line-number.split-diff-removed,
atom-text-editor .line.split-diff-removed,
atom-text-editor::shadow .line.split-diff-removed {
  background-color: rgba(224, 82, 82, 0.4);
}
atom-text-editor .gutter .line-number.split-diff-selected,
atom-text-editor::shadow .gutter .line-number.split-diff-selected,
atom-text-editor .line.split-diff-selected,
atom-text-editor::shadow .line.split-diff-selected {
  background-color: rgba(224, 194, 133, 0.4);
}
atom-text-editor .split-diff-word-added .region,
atom-text-editor::shadow .split-diff-word-added .region {
  background-color: rgba(67, 208, 138, 0.5);
}
atom-text-editor .split-diff-word-removed .region,
atom-text-editor::shadow .split-diff-word-removed .region {
  background-color: rgba(224, 82, 82, 0.5);
}
atom-text-editor .split-diff-offset,
atom-text-editor::shadow .split-diff-offset {
  background-color: #2c313a;
}
/* loading modal */
@keyframes delay-modal {
  0% {
    opacity: 0;
  }
  99% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
.split-diff-hide-mask {
  animation: delay-modal 1s forwards;
}
.split-diff-modal {
  padding: 14px;
  text-align: center;
}
.split-diff-message {
  font-size: 26px;
  display: inline-block;
}
.split-diff-icon {
  background-image: url('images/octocat-spinner-128.gif');
  background-repeat: no-repeat;
  background-size: contain;
  width: 40px;
  height: 40px;
  display: inline-block;
}
.split-diff-hide-mask:after {
  display: none;
}
/* bottom panel UI */
.split-diff-ui {
  padding: 2px 10px 2px 10px;
  display: flex;
  justify-content: space-between;
  /* hard code width so mid column is actually in middle */
  /* hard code width so mid column is actually in middle */
  /* buttons */
