}
.split-diff-ui .left {
  width: 300px;
}
.split-diff-ui .mid {
  text-align: center;
}
.split-diff-ui .right {
  display: flex;
  width: 300px;
}
.split-diff-ui .num-diff,
.split-diff-ui .selection-count,
.split-diff-ui .selection-divider,
.split-diff-ui .num-diff-text {
  display: inline-block;
  margin-left: 0.5em;
  font-size: 1rem;
}
.split-diff-ui .selection-count,
.split-diff-ui .num-diff {
  vertical-align: middle;
}
.split-diff-ui .copy-to-left::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 16px;
  width: 16px;
  height: 16px;
  content: "\f035";
}
.split-diff-ui .copy-to-right::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 16px;
  width: 16px;
  height: 16px;
  content: "\f035";
}
.split-diff-ui .copy-to-right:before {
  transform: scaleX(-1);
}
.split-diff-ui .prev-diff::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 16px;
  width: 16px;
  height: 16px;
  content: "\f0a2";
}
.split-diff-ui .next-diff::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 16px;
  width: 16px;
  height: 16px;
  content: "\f0a3";
}
.split-diff-ui .prev-diff,
.split-diff-ui .next-diff,
.split-diff-ui .copy-to-left {
  margin-right: 0.5rem;
}
.split-diff-ui .ignore-whitespace-label {
  align-self: center;
  margin-left: auto;
  margin-bottom: 0;
  cursor: pointer;
  padding-right: 4px;
}
.split-diff-ui input[type=checkbox] {
  display: none;
}
.split-diff-ui input[type=checkbox] + label:before {
  content: '\f081';
}
.split-diff-ui input[type=checkbox]:checked + label:before {
  content: '\f03a';
}
.split-diff-ui label:before {
  font-family: 'Octicons Regular';
  display: inline-block;
  cursor: pointer;
  padding: 0 0.5rem;
  font-size: 1rem;
  margin-right: 0.5rem;
}
